ValidationRule.errorConditionFormula:
AND( /* only for Career record type */ $RecordType.DeveloperName = "Career",
/* enforce on AA Received or any subsequent non-terminal stage */ OR( ISPICKVAL(Stage__c, "AA Received"), ISPICKVAL(Stage__c, "Create Card in Capstone ALC Trackers"), ISPICKVAL(Stage__c, "Candidate Complete"), ISPICKVAL(Stage__c, "Waiting on License to Post"), ISPICKVAL(Stage__c, "Contract A or B CLXP Codes Sent"), ISPICKVAL(Stage__c, "Training"), ISPICKVAL(Stage__c, "ALC Trackers") ),
/* any of the required fields is blank */ OR( ISBLANK(MMID__c), ISBLANK(Mobile__c), ISBLANK(TEXT(Position__c)), ISBLANK(Start_date__c) ) )
