ValidationRule.errorConditionFormula:
AND(
  /* MD200 required at Stage ≥3 */
  VALUE(
    LEFT(
      TEXT(Highest_Level_Achieved__c),
      FIND("-", TEXT(Highest_Level_Achieved__c)) - 1
    )
  ) >= 3 &&
  ISBLANK(TEXT(MDE200__c)) ||
  /* MS-10 & Career PPT required at Stage ≥4 */
  (
    VALUE(
      LEFT(
        TEXT(Highest_Level_Achieved__c),
        FIND("-", TEXT(Highest_Level_Achieved__c)) - 1
      )
    ) >= 4 &&
    (
      ISBLANK(TEXT(MS_10__c)) ||
      ISBLANK(TEXT(Career_Presentation_ppt__c))
    )
  )
)
